Event Start
/* Generated by DVelop Work With Plus Pattern [Start] - Do not change */
LoadWWPContext.Call(&WWPContext)
If &HttpRequest.Method = HttpMethod.Get For Each Where SecUserId = &SecUserId When None msg('Record not found') EndFor
For Each &Exist = false For Each Where SecUserId = &SecUserId &Exist = true EndFor &Description = SecRoleDescription + ' (' + SecRoleName + ')' If &Exist &InsertIndex = 1 Do while &InsertIndex <= &AddedDscList.Count AND &AddedDscList.Item(&InsertIndex) < &Description &InsertIndex = &InsertIndex + 1 EndDo &AddedKeyList.Add(SecRoleId, &InsertIndex) &AddedDscList.Add(&Description, &InsertIndex) Else &InsertIndex = 1 Do while &InsertIndex <= &NotAddedDscList.Count AND &NotAddedDscList.Item(&InsertIndex) < &Description &InsertIndex = &InsertIndex + 1 EndDo &NotAddedKeyList.Add(SecRoleId, &InsertIndex) &NotAddedDscList.Add(&Description, &InsertIndex) EndIf EndFor Do 'SaveLists' EndIf &AddedKeyListXml.Visible = false &NotAddedKeyListXml.Visible = false &AddedDscListXml.Visible = false &NotAddedDscListXml.Visible = false
