Sub 'UpdateAssociationVariables'
/* Generated by DVelop Work With Plus Pattern [Start] - Do not change */
Do 'LoadLists' &AssociatedRecords.Clear() &NotAssociatedRecords.Clear() &i = 1 For &SecRoleId in &AddedKeyList &Description = &AddedDscList.Item(&i) &AssociatedRecords.AddItem(&SecRoleId, &Description.Trim()) &i += 1 EndFor &i = 1 For &SecRoleId in &NotAddedKeyList &Description = &NotAddedDscList.Item(&i) &NotAssociatedRecords.AddItem(&SecRoleId, &Description.Trim()) &i += 1 EndFor
/* Generated by DVelop Work With Plus Pattern [End] - Do not change */
EndSub
Sub 'Show Error Messages'
/* Generated by DVelop Work With Plus Pattern [Start] - Do not change */
For &Message in &SecUserRole.GetMessages() If &Message.Type = MessageTypes.Error msg(&Message.Description) EndIf EndFor
/* Generated by DVelop Work With Plus Pattern [End] - Do not change */
EndSub
Sub 'LoadLists'
/* Generated by DVelop Work With Plus Pattern [Start] - Do not change */
If not &AddedKeyListXml.IsEmpty() &AddedDscList.FromXml(&AddedDscListXml) &AddedKeyList.FromXml(&AddedKeyListXml) EndIf If not &NotAddedKeyListXml.IsEmpty() &NotAddedKeyList.FromXml(&NotAddedKeyListXml) &NotAddedDscList.FromXml(&NotAddedDscListXml) EndIf
/* Generated by DVelop Work With Plus Pattern [End] - Do not change */
EndSub
Sub 'SaveLists'
/* Generated by DVelop Work With Plus Pattern [Start] - Do not change */
If &AddedKeyList.Count > 0 &AddedKeyListXml = &AddedKeyList.ToXml() &AddedDscListXml = &AddedDscList.ToXml() else &AddedKeyListXml = '' &AddedDscListXml = '' EndIf If &NotAddedKeyList.Count > 0 &NotAddedKeyListXml = &NotAddedKeyList.ToXml() &NotAddedDscListXml = &NotAddedDscList.ToXml() else &NotAddedKeyListXml = '' &NotAddedDscListXml = '' EndIf
/* Generated by DVelop Work With Plus Pattern [End] - Do not change */
EndSub
Sub 'AssociateAll'
/* Generated by DVelop Work With Plus Pattern [Start] - Do not change */
Do 'LoadLists' &i = 1 &InsertIndex = 1 For &SecRoleId in &NotAddedKeyList &Description = &NotAddedDscList.Item(&i) Do while &InsertIndex <= &AddedDscList.Count AND &AddedDscList.Item(&InsertIndex) < &Description &InsertIndex = &InsertIndex + 1 Enddo &AddedKeyList.Add(&SecRoleId, &InsertIndex) &AddedDscList.Add(&Description, &InsertIndex) &i = &i + 1 EndFor &NotAddedKeyList.Clear() &NotAddedDscList.Clear() Do 'SaveLists'
/* Generated by DVelop Work With Plus Pattern [End] - Do not change */
EndSub
Sub 'AssociateSelected'
/* Generated by DVelop Work With Plus Pattern [Start] - Do not change */
Do 'LoadLists' &i = 1 For &SecRoleId in &NotAddedKeyList If &SecRoleId = &NotAssociatedRecords Exit EndIf &i = &i + 1 EndFor If &i <= &NotAddedKeyList.Count &Description = &NotAddedDscList.Item(&i) &InsertIndex = 1 Do while &InsertIndex <= &AddedDscList.Count AND &AddedDscList.Item(&InsertIndex) < &Description &InsertIndex = &InsertIndex + 1 Enddo &AddedKeyList.Add(&NotAssociatedRecords, &InsertIndex) &AddedDscList.Add(&Description, &InsertIndex) &NotAddedKeyList.Remove(&i) &NotAddedDscList.Remove(&i) Do 'SaveLists' EndIf Do 'UpdateAssociationVariables'
/* Generated by DVelop Work With Plus Pattern [End] - Do not change */
EndSub
Sub 'DisassociateSelected'
/* Generated by DVelop Work With Plus Pattern [Start] - Do not change */
Do 'LoadLists' &i = 1 For &SecRoleId in &AddedKeyList If &SecRoleId = &AssociatedRecords exit EndIf &i = &i + 1 EndFor If &i <= &AddedKeyList.Count &Description = &AddedDscList.Item(&i) &InsertIndex = 1 Do while &InsertIndex <= &NotAddedDscList.Count AND &NotAddedDscList.Item(&InsertIndex) < &Description &InsertIndex = &InsertIndex + 1 Enddo &NotAddedKeyList.Add(&AssociatedRecords, &InsertIndex) &NotAddedDscList.Add(&Description, &InsertIndex) &AddedKeyList.Remove(&i) &AddedDscList.Remove(&i) Do 'SaveLists' EndIf Do 'UpdateAssociationVariables'
/* Generated by DVelop Work With Plus Pattern [End] - Do not change */
EndSub


