Event Start

	DVPanel_TableLogin.ShowHeader = false
	RegisterUser.Link = GAMRegisterUser.Link()

	//Used when multiple repository connections exist  ////////////////////////////
	&ConnectionInfoCollection = GAM.GetConnections()
	If &ConnectionInfoCollection.Count > 0 and GAMRepository.GetId().IsEmpty()
		//The first connection found is established by default 
		&isOK = GAM.SetConnection(&ConnectionInfoCollection.Item(1).Name, &Errors)
	EndIf
	///////////////////////////////////////////////////////////////////////////////

	//Display authentication Facebook and Twitter if it is Enabled ////////////////
	Facebook.Visible = False
	Google.Visible	= False
	Twitter.Visible	= False
	If GAMRepository.CanAuthenticateWith(GAMAuthenticationTypes.Facebook)
		Facebook.Visible = True
		Facebook.FromImage(GAMButtonFacebook)
	Endif
	If GAMRepository.CanAuthenticateWith(GAMAuthenticationTypes.Google)
		Google.Visible = True
		Google.FromImage(GAMButtonGoogle)
	Endif
	If GAMRepository.CanAuthenticateWith(GAMAuthenticationTypes.Twitter)
		Twitter.Visible = True
		Twitter.FromImage(GAMButtonTwitter)
	Endif
	///////////////////////////////////////////////////////////////////////////////

	/* Generated by DVelop Work With Plus Pattern [Start] - Do not change */
/* Generated by DVelop Work With Plus Pattern [End] - Do not change */

EndEvent

Event Enter

	// Set Remember User type //////////////////////////////////
	If &KeepMeLoggedIn
		&AdditionalParameter.RememberUserType = iif(&KeepMeLoggedIn, GAMRememberUserTypes.Authentication, GAMRememberUserTypes.None)
	Else
		If &RememberMe 
			&AdditionalParameter.RememberUserType = iif(&RememberMe, GAMRememberUserTypes.Login, GAMRememberUserTypes.None)
		Else
			&AdditionalParameter.RememberUserType = GAMRememberUserTypes.None
		Endif
	Endif
	////////////////////////////////////////////////////////////


	//Send Custom Properties (optional) ////////////////////////
	//&CustomProperty.Id		= "branch"
	//&CustomProperty.Value	= "120"
	//&AdditionalParameter.Properties.Add(&CustomProperty)
	////////////////////////////////////////////////////////////

	&AdditionalParameter.AuthenticationTypeName = &LogOnTo
	// Login User //////////////////////////////////////////////
	&LoginOK = GAMRepository.Login(&UserName, &UserPassword, &AdditionalParameter, &Errors )
	If &LoginOK
		//Get my custom application data in json format ////////////
		//The &ApplicationData is saved in the Login action when authentication type is "Web service" or "Custom"
		&ApplicationData = GAMSession.GetApplicationData()
		If not &ApplicationData.IsEmpty()
			&GAMExampleSDTApplicationData.FromJson(&ApplicationData)
		Endif
		////////////////////////////////////////////////////////////

		////////////////////////////////////////////////////////////
		//**Uncomment the following lines if you use the property Web User Experience = Smooth
		//
		//&URL = GAMRepository.GetLastErrorsURL()
		//If &URL.IsEmpty()
		//	GAMHome()
		//Else
		//	Link(&URL)
		//Endif
		////////////////////////////////////////////////////////////	
	Else
		If &Errors.Count > 0 
			Do Case
			Case (&Errors.Item(1).Code = GAMErrorMessages.UserPasswordExpired   OR  &Errors.Item(1).Code = GAMErrorMessages.UserPasswordMustBeChanged)
				//Webpanel to Change Password
				GAMChangePassword(&LogOnTo, &UserName, &UserRememberMe )
			Case &Errors.Item(1).Code = GAMErrorMessages.UserMissingRequiredData
				//Webpanel to Complete User data
				GAMUpdateRegisterUser()
			EndCase
		EndIf
	Endif
	////////////////////////////////////////////////////////////

	/* Generated by DVelop Work With Plus Pattern [Start] - Do not change */
/* Generated by DVelop Work With Plus Pattern [End] - Do not change */

EndEvent

