Event Refresh

	&isRedirect = False
	//Gets last error in the GAM /////////////////////////
	&ErrorsLogin = GAMRepository.GetLastErrors()
	If &ErrorsLogin.Count > 0
		If &ErrorsLogin.Item(1).Code = GAMErrorMessages.UserMissingRequiredData
			//Webpanel to Complete User data
			GAMUpdateRegisterUser()
			&isRedirect = True
		Else
			&UserPassword = ""
			&Errors = &ErrorsLogin
			Do 'DisplayMessages'
		Endif
	Endif
	//////////////////////////////////////////////////////

	If not &isRedirect
		//Gets last error in GAM /////////////////////////////	
		&SessionValid = GAMSession.IsValid(&Session, &Errors)

		If &SessionValid and not &Session.IsAnonymous	
			&URL = GAMRepository.GetLastErrorsURL()
			If &URL.IsEmpty()
				GAMHome()
			Else
				Link(&URL)
			Endif
		Else
			// List Log on to ///////////////////////////////////////////////////////////
			&LogOnTo.Clear()
			&AuthenticationTypes = GAMRepository.GetEnabledAuthenticationTypes(&Language, &Errors)
			For &AuthenticationType in &AuthenticationTypes
				If &AuthenticationType.NeedUserName //Authentication types you must enter the username in this web panel
					&LogOnTo.AddItem(&AuthenticationType.Name,&AuthenticationType.Description)
				EndIf
			EndFor
			do 'AttributesSecurityCode'
			//////////////////////////////////////////////////////////////////////////////

			//Initialize variables if the user chooses to keep that information  ////////
			&isOK = GAMRepository.GetRememberLogin(&LogOnTo, &UserName, &UserRememberMe, &Errors)
			If &UserRememberMe = GAMRememberUserTypes.Login
				&RememberMe = True
			Endif
			//////////////////////////////////////////////////////////////////////////////

			// Select Default Authentication Type, Display Remember User if it is enabled on Repository /////////////////////		
			&Repository = GAMRepository.Get()
			If &LogOnTo.Count > 1
				&LogOnTo = &Repository.DefaultAuthenticationTypeName
			Endif		
			&RememberMe.Visible			= False
			&KeepMeLoggedIn.Visible	= False
			Do Case
			Case &Repository.UserRememberMeType = GAMRepositoryRememberUserTypes.Login
				&RememberMe.Visible = True
			Case &Repository.UserRememberMeType = GAMRepositoryRememberUserTypes.Authentication
				&KeepMeLoggedIn.Visible	= True
			Case &Repository.UserRememberMeType = GAMRepositoryRememberUserTypes.Both
				&RememberMe.Visible		= True
				&KeepMeLoggedIn.Visible	= True
			EndCase
			//////////////////////////////////////////////////////////////////////////////
		EndIf
	Endif

EndEvent

