Sub 'DisplayMessages'

	For &Error in &Errors
		If &Error.Code <> GAMErrorMessages.SessionExpired
			Msg(Format(!"%1 (GAM%2)", &Error.Message, &Error.Code))
		Endif
	EndFor

EndSub

