Event Grid.Load

  &GridPageSize = Grid.Rows
  &Application.Load(&ApplicationId)
	&AppId = &ApplicationId
	&Filter.Name			  = !"%" + &FilName
	&Filter.AccessTypeDefault.FromString(&PermissionAccessTypeDefault)
	&Filter.TypeFilter.FromString(&PermissionTypeFilter)
	&Filter.IsAutomaticPermission.FromString(&isAutomaticPermission)
	&Permissions=&Application.GetPermissions(&Filter, &Errors)
	If &Permissions.Count = 0
		&GridPageCount = 0
	Else
		&GridPageCount = (&Permissions.Count/&GridPageSize) + iif(Mod(&Permissions.Count, &GridPageSize) > 0, 1, 0)
	EndIf
	&GridRecordCount = &Permissions.Count
	For &AppPermission in &Permissions
		&Id			= &AppPermission.GUID
		&Name 		= &AppPermission.Name
		&Dsc 		= GetMessageText(&AppPermission.Description)
		&AccessType = &AppPermission.AccessType

	/* Generated by DVelop Work With Plus Pattern [Start] - Do not change */
/* Generated by DVelop Work With Plus Pattern [End] - Do not change */

		Grid.Load()
	EndFor

EndEvent

