Event Grid.Load

	&GAMUser.Load(&UserId)
	&GAMUserName = &GAMUser.Name
	&GAMApplication.Load(&ApplicationId)
	&PermissionFilter.ApplicationId = &ApplicationId
	&PermissionFilter.Name	= !"%" + &Search
	&PermissionFilter.AccessType.FromString(&PermissionAccessType)
	&PermissionFilter.Inherited.FromString(&BoolenFilter)
	If not &ApplicationId.IsEmpty()
		&GAMPermissions = &GAMUser.GetUnassignedPermissions(&PermissionFilter, &Errors)
		if &GamPermissions.count=0
			&GridPageCount=0
		else
			&GridPageCount=(&GamPermissions.count/Page.Rows)+1
		endif
		&GridRecordCount = &GamPermissions.Count
		For &Permission in &GAMPermissions
			&CountPermissions+= 1
			&Id		= &Permission.GUID
			&Name 	= &Permission.Name
			&Dsc 	= &Permission.Description

	/* Generated by DVelop Work With Plus Pattern [Start] - Do not change */
/* Generated by DVelop Work With Plus Pattern [End] - Do not change */

		Grid.Load()
	EndFor
	Endif

EndEvent



Event 'DoAdd'

	&GAMUser.Load(&UserId)
	For each line
		If &Select
			&PermissionAdd.ApplicationId= &ApplicationId
			&PermissionAdd.GUID		  	= &Id
			&PermissionAdd.Type.FromString(&Access)
			&isOK = &GAMUser.AddPermission(&PermissionAdd, &Errors)
			If not &isOK
				For &Error in &Errors
					Msg(Format(!"%1 (GAM%2)", &Error.Message, &Error.Code))
				EndFor	
				Exit
			Endif		
		Endif
	EndFor
	If &isOK
		Commit
		GAMWWUserPermissions(&UserId, &ApplicationId)
	Else
		For &Error in &Errors
			Msg(Format(!"%1 (GAM%2)", &Error.Message, &Error.Code))
		EndFor		
	Endif

	/* Generated by DVelop Work With Plus Pattern [Start] - Do not change */
/* Generated by DVelop Work With Plus Pattern [End] - Do not change */

EndEvent





