Event Grid.Load

	&GridPageSize = Grid.Rows
  &Application.Load(&ApplicationId)
	if not &FilName.IsEmpty()
		&Filter.Name	= !"%" + &FilName	
	endif	
	&GamWWAppMenus = &Application.GetMenus(&Filter, &Errors)
	If &GamWWAppMenus.Count = 0
		&GridPageCount = 0
	Else
		&GridPageCount = (&GamWWAppMenus.Count / &GridPageSize) + iif(Mod(&GamWWAppMenus.Count, &GridPageSize) > 0, 1, 0)
	EndIf
	&GridRecordCount = &GamWWAppMenus.Count
	For &AppMenu in &GamWWAppMenus
		&Id			= &AppMenu.Id
		&Name 		= &AppMenu.Name
		&Dsc 		= &AppMenu.Description

	/* Generated by DVelop Work With Plus Pattern [Start] - Do not change */
/* Generated by DVelop Work With Plus Pattern [End] - Do not change */

		Grid.Load()
	EndFor

EndEvent

